mapper.insert:
INSERT INTO TB_INQUIRY(INQUIRY_NO
					, INQUIRY_TYPE
                    , INQUIRY_CONTENT
                    , MEMBER_ID
                    , PROD_NO)
               VALUES(SEQ_INQUIRY_NO.NEXTVAL
               		, #{inquiryType}
                    , #{inquiryContent}
                    , #{memberId}
                    , #{prodNo})

mapper.update:
UPDATE TB_INQUIRY
   SET INQUIRY_STATUS = 'N'
 WHERE INQUIRY_NO = #{inquiryNo}

UPDATE TB_INQUIRY
   SET INQUIRY_CONTENT = #{inquiryContent}
 WHERE INQUIRY_NO = #{inquiryNo}
   AND INQUIRY_STATUS = 'Y'

mapper.select:
SELECT COUNT(*)
FROM TB_WISH
WHERE MEMBER_ID = #{memberId}
  AND PROD_NO = #{prodNo}

mapper.delete:
    DELETE FROM TB_WISH
	 WHERE MEMBER_ID = #{memberId}
	   AND PROD_NO = #{prodNo}

mapper.select:
SELECT CART_NO, MEMBER_ID, PROD_NO, OPT_NO, CART_QTY
  FROM TB_CART
 WHERE MEMBER_ID = #{memberId}
   AND OPT_NO = #{optNo}

mapper.update:
UPDATE TB_CART
   SET CART_QTY = #{cartQty}
 WHERE CART_NO = #{cartNo}
